Ext.define('MyApp.controller.controladorProvinciasBusquedaAvanzada', {
    extend: 'Ext.app.Controller',

    config: {
        refs: {
            list: '#listaProvinciasBusquedaAvanzada'
        },

        control: {
            "list": {
                itemtap: 'onListItemTap'
            }
        }
    },

    onListItemTap: function(dataview, index, target, record, e, options) {
        //Con esta linea se muestra el panel de busqueda avanzada que da las provincias
        Ext.getCmp("panelProvinciasBusquedaAvanzada").setHidden(1);
        //Y con esta linea se escribe el Text en el boton de provincias en busq
        //avanzada
        Ext.getCmp("botonProvinciaBusquedaAvanzada").setText("<p style='font-size: 60%;'> " +record.get("nombreProvincia")+ "</p>");
        Ext.getCmp("botonProvinciaBusquedaAvanzada").setItemId(record.get("nombreProvincia"));
        //Y finalmente, cuando se le da a una provincia, deben salir los cantones
        //correspondientes
        Ext.getStore("storeCantonBusquedaAvanzada").getProxy().setExtraParam('provincia',record.get("idProvincia"));
        Ext.getStore("storeCantonBusquedaAvanzada").load();
    }

});