Ext.define('MyApp.store.storeSubEventos', {
    extend: 'Ext.data.Store',

    requires: [
        'MyApp.model.modelSubEventos'
    ],

    config: {
        autoSync: true,
        clearOnPageLoad: false,
        model: 'MyApp.model.modelSubEventos',
        pageSize: 15,
        storeId: 'storeSubEventos',
        proxy: {
            type: 'jsonp',
            url: 'http://www.didicr.com/php/evento/eventoCategoria.php',
            reader: {
                type: 'json',
                rootProperty: 'ptm'
            }
        },
        grouper: {
            groupFn: function(record) {
                var fechaParaGrouperListaCatEventos= new Date();

                //alert(record.get("fecha"));

                mesParaGrouperListaCatEventos=fechaParaGrouperListaCatEventos.getMonth()+1;

                if(fechaParaGrouperListaCatEventos.getFullYear()==record.get("fecha").slice(0,4) && mesParaGrouperListaCatEventos==record.get("fecha").slice(5,7) && fechaParaGrouperListaCatEventos.getDate()==record.get("fecha").slice(8)){
                    return "Hoy";
                }
                else if(fechaParaGrouperListaCatEventos.getFullYear()==record.get("fecha").slice(0,4) && mesParaGrouperListaCatEventos==record.get("fecha").slice(5,7) && (fechaParaGrouperListaCatEventos.getDate()+1)==record.get("fecha").slice(8)){
                    return "Ma√±ana";
                }
                else{
                    stringDevolucionFecha=record.get("fecha").slice(8)+"-"+record.get("fecha").slice(5,7)+"-"+record.get("fecha").slice(0,4);
                    return stringDevolucionFecha;
                }
            },
            sortProperty: 'fecha'
        },
        listeners: [
            {
                fn: 'onJsonpstoreLoad',
                event: 'load'
            }
        ]
    },

    onJsonpstoreLoad: function(store, records, successful, operation, eOpts) {
        Ext.getCmp("tabPanelPrincipal").setActiveItem(Ext.getCmp("containerSubEventos"));

        Ext.getCmp("containerPrincipalEventos").setMasked(false);
    }

});